import React, { useRef, useState } from 'react'
import "../assets/css/video.css"

export default function Video() {
    const videoRef = useRef(null);
    const [playing, setPlaying] = useState(true);
    videoRef.current.play();

    const handleVideoPress = () => {
        // If video is playing already, stop it and vice versa
        if (playing) {
            videoRef.current.pause();
            setPlaying(false);
        } else {
            videoRef.current.play();
            setPlaying(true);
        }
    }

    return (
        <div className="video">
            <video className="video__player" loop muted ref={videoRef} onClick={handleVideoPress}>
                <source src="https://download1506.mediafire.com/ih8lk7gbi4ig/kwon6hj7sgyelhx/Lizard+sneaks+up+on+dog+panics+when+spotted+_+%F0%9F%A6%8E+%F0%9F%90%B6+%F0%9F%A4%A3+shorts.mp4" type="video/mp4"></source>
                Your browser does not support video
            </video>
        </div>
    )
}
